    function addToCart(productId, brand, size, quantity, checkout) {

        checkout = typeof checkout !== 'undefined' ? checkout : false;

        var dataString = 'productId=' + productId + '&brand=' + brand + '&size=' + size + '&quantity=' + quantity;
        //console.log(dataString);

        $.ajax({
            url: site_url + 'checkout/addtocart',
            type: 'POST',
            data: dataString,
            success: function (response, textStatus, jqXHR) {
                //console.log(response);
                response = JSON.parse(response);
                if (response.status == 'success') {

                    if (!checkout) {
                        updateCart(true);

                        $('#select-size .select').html('Select your size');
                        $('#select-size option[value="0"]').prop('selected', true);
                        $('#select-quantity .select').html('1x');
                        $('#select-quantity option[value="1"]').prop('selected', true);
                        $('body').removeClass('adding-to-cart');
                    } else {
                        window.location = site_url + 'checkout';
                    }

                    // FB
                    var ids = new Array();
                    ids.push(productId);

                    fbq('track', 'AddToCart', {
                        content_ids: ids,
                        content_type: 'product',
                        value: 0,
                        currency: 'EUR'
                    });

                } else if (response.status == 'error') {
                    openNotification('Oops', 'AJAX is ok but something went wrong', true);
                    $('body').removeClass('adding-to-cart');
                }
            },
            error: function (response, jqXHR, textStatus, errors) {
                //console.log(response);
                openNotification('Oops', 'Something went wrong', true);
                $('body').removeClass('adding-to-cart');
            }
        });

    }

    function addToCartManual(productId, brand, size, quantity, checkout) {

        checkout = typeof checkout !== 'undefined' ? checkout : false;

        var dataString = 'productId=' + productId + '&brand=' + brand + '&size=' + size + '&quantity=' + quantity;

        $.ajax({
            url: site_url + 'checkout/addtocart',
            type: 'POST',
            data: dataString,
            success: function (response, textStatus, jqXHR) {
                //console.log(response);
                response = JSON.parse(response);
                if (response.status == 'success') {

                    //updateCart(true);
                    updateCheckout();
                    updateCart(false);
                    $('body').removeClass('adding-to-cart');

                } else if (response.status == 'error') {
                    openNotification('Oops', 'AJAX is ok but something went wrong', true);
                    $('body').removeClass('adding-to-cart');
                }
            },
            error: function (response, jqXHR, textStatus, errors) {
                //console.log(response);
                openNotification('Oops', 'Something went wrong', true);
                $('body').removeClass('adding-to-cart');
            }
        });

    }

