/***
 * Algolia search
 */

if ($('.search-result').length !== 0) {
    var algoliaClient = algoliasearch('DNDWRPAHY0', 'ea796b27798d09faded37e091e0445d2');
}

function searchFor(terms) {
    var index = algoliaClient.initIndex('products');

    index.search(terms, function searchDone(err, content) {
        var products = content.hits;
        var gridContent = '';
        for (i = 0; i < products.length; i++) {
            var link = products[i].link;
            if ('english' == lang) {
                link = link.replace('.nl', '.com');
            }
            gridContent += '<a href="' + link + '" title="' + products[i].title + '"><div class="grid-item grid-item-basic" data-pid="' + products[i].id + '">' +
                '<div class="grid-item-content">' +
                '<div class="grid-model">' +
                '<span class="grid-brand">' + products[i].brand + '</span><br>' +
                products[i].title +
                '</div>' +
                '<div class="grid-price"><span class="euro">&euro;</span>' + products[i].price + '</div>' +
                '<div class="grid-image group">' +
                '<img src="' + products[i].image_link + '">' +
                '<div class="shadow"></div>' +
                '</div>' +
                '</div>' +
                '<div class="grid-label label-restocked">' + products[i].availability + '</div>' +
                '</div></a>';

        }
        $('#grid-container .grid-item').remove();
        $('#grid-container').append(gridContent);
        $('#grid, #grid-container').css({height: 'auto'});

        if (products.length > 0) {
            if ('dutch' == lang) {
                $('.result-count').text(products.length + ' producten gevonden');
            } else {
                $('.result-count').text(products.length + ' products found');
            }
        } else {
            $('.result-count').text('');
        }

    });
}

$(function () {
    $('#searchbar-form #s').focus();

    $(document).on('keyup', '#s', function () {
        var terms = $(this).val();
        if (terms.length >= 3) {
            searchFor(terms);
        } else {
            $('#grid-container .grid-item').remove();
            $('.result-count').text('');
        }
    });

    $(document).on('submit', '#searchbar-form', function (event) {
        event.preventDefault();
        var terms = $('#searchbar-form #s').val();
        if (terms.length > 0) {
            searchFor(terms);
        }
    });
});

$(function () {
    if ($('form#newsletter-signup-form').length !== 0) {
        $('form#newsletter-signup-form').submit(function (event) {
            event.preventDefault();
        });

        $('form#newsletter-signup-form').parsley().on('form:success', function (formInstance) {
            submitNewsletterForm('form#newsletter-signup-form');
        });
    }
});

var newsletterRequest;
function submitNewsletterForm(id) {
    $(id).addClass('is-sending');

    var data = {
        email: $(id).find('input[name="n_email"]').val(),
        gender: $(id).find('input[name="n_gender"]:checked').val()
    }

    console.log(data);

    newsletterRequest = $.ajax({
        type: 'POST',
        url: site_url + 'newsletter/subscribe',
        data: data,
        beforeSend: function (request) {
            if (newsletterRequest != null) {
                newsletterRequest.abort();
            }
        },
        success: function (response) {
            console.log(response);
            if (response.status == 'success') {
                $(id).find('input[type="email"]').val('');
                $(id).find('.focus-filled').removeClass('focus-filled');
                $(id).find('input[type="radio"]').prop('checked', false);
                $(id).find('.parsley-success').removeClass('parsley-success');
                $(id).find('.parsley-error').removeClass('parsley-error');

                $(id).find('.form-response').html('<span>' + response.message + '</span>');
                setTimeout(function () {
                    $(id).find('.form-response').empty();
                }, 5000);
            }
        },
        error: function (response) {
            console.log(response);
        },
        complete: function () {
            $(id).removeClass('is-sending');
        }
    });
}